-- 주행 기록 테이블 인덱스
CREATE INDEX IF NOT EXISTS idx_ridingRecords_userId ON ridingRecords(userId);
CREATE INDEX IF NOT EXISTS idx_ridingRecords_date ON ridingRecords(date);
CREATE INDEX IF NOT EXISTS idx_ridingRecords_userId_date ON ridingRecords(userId, date);

-- 게시글 테이블 인덱스
CREATE INDEX IF NOT EXISTS idx_posts_userId ON posts(userId);
CREATE INDEX IF NOT EXISTS idx_posts_createdAt ON posts(createdAt);
CREATE INDEX IF NOT EXISTS idx_posts_postType ON posts(postType);

-- 댓글 테이블 인덱스
CREATE INDEX IF NOT EXISTS idx_comments_postId ON comments(postId);
CREATE INDEX IF NOT EXISTS idx_comments_userId ON comments(userId);

-- 좋아요 테이블 인덱스
CREATE INDEX IF NOT EXISTS idx_postLikes_postId ON postLikes(postId);
CREATE INDEX IF NOT EXISTS idx_postLikes_userId ON postLikes(userId);
CREATE INDEX IF NOT EXISTS idx_postLikes_postId_userId ON postLikes(postId, userId);

-- 친구 테이블 인덱스
CREATE INDEX IF NOT EXISTS idx_friends_userId1 ON friends(userId1);
CREATE INDEX IF NOT EXISTS idx_friends_userId2 ON friends(userId2);

-- 팔로우 테이블 인덱스
CREATE INDEX IF NOT EXISTS idx_follows_followerId ON follows(followerId);
CREATE INDEX IF NOT EXISTS idx_follows_followingId ON follows(followingId);

-- 알림 테이블 인덱스
CREATE INDEX IF NOT EXISTS idx_notifications_userId ON notifications(userId);
CREATE INDEX IF NOT EXISTS idx_notifications_userId_isRead ON notifications(userId, isRead);

-- 그룹 세션 테이블 인덱스
CREATE INDEX IF NOT EXISTS idx_groupSessions_hostId ON groupSessions(hostId);
CREATE INDEX IF NOT EXISTS idx_groupSessions_isActive ON groupSessions(isActive);

-- 그룹 멤버 테이블 인덱스
CREATE INDEX IF NOT EXISTS idx_groupMembers_groupId ON groupMembers(groupId);
CREATE INDEX IF NOT EXISTS idx_groupMembers_userId ON groupMembers(userId);
CREATE INDEX IF NOT EXISTS idx_groupMembers_groupId_status ON groupMembers(groupId, status);

-- 그룹 메시지 테이블 인덱스
CREATE INDEX IF NOT EXISTS idx_groupMessages_groupId ON groupMessages(groupId);
CREATE INDEX IF NOT EXISTS idx_groupMessages_groupId_createdAt ON groupMessages(groupId, createdAt);

-- 스쿠터 테이블 인덱스
CREATE INDEX IF NOT EXISTS idx_scooters_userId ON scooters(userId);

